import axios from 'axios';

// const apiUrl = "http://localhost:5144"
axios.defaults.baseURL = 'http://localhost:5144';
export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/items`)    
    return result.data;
  },

  addTask: async(name)=>{
    console.log('addTask', name)
    const result = await axios.post(`${apiUrl}/items?name=${name}`)    
    return result.data;
  },

  setCompleted: async(id, isComplete)=>{
    console.log('setCompleted', {id, isComplete})
    const result = await axios.patch(`${apiUrl}/items?id=${id}&isComplete=${isComplete}`)    
    return result.data;
  },

  deleteTask:async(id)=>{
    console.log('deleteTask')
    const result = await axios.delete(`${apiUrl}/items?id=${id}`)    
    return result.data;
  }
};
